-- SHA1 produces a 120-bit object
-- we want something like n=256 of these for a sketch.

-- see http://www.postgresql.org/docs/current/static/functions-binarystring.html 
-- and http://www.postgresql.org/docs/8.4/static/functions-bitstring.html
-- for PGSQL bitmap manipulation

-- see http://www.postgresql.org/docs/8.4/static/pgcrypto.html for info on PGCrypto
-- PGSQL's SHA1 is 20 bytes long.

DROP FUNCTION IF EXISTS sql_rightmost_one(bytea) cascade;
CREATE FUNCTION sql_rightmost_one(bits bytea) RETURNS integer AS 
$$
DECLARE
len integer := length(bits)*8 - 1;
BEGIN
WHILE len > 0 LOOP
  IF get_bit(bits, len) 
  THEN 
--    RAISE NOTICE 'sql_rightmost_one is %', len;
    RETURN len;
  END IF;
  len := len - 1;
END LOOP;
RETURN 0; 
END;
$$ LANGUAGE PLPGSQL;

DROP FUNCTION IF EXISTS sql_leftmost_zero(bytea) cascade;
CREATE FUNCTION sql_leftmost_zero(bits bytea) RETURNS integer AS 
$$
DECLARE
len integer;
i integer;
BEGIN
IF bits IS NULL THEN
  RAISE NOTICE 'null arg to sql_leftmost_zero';
END IF;
len := length(bits)*8 - 1;
FOR i in 0..len LOOP
  IF get_bit(bits, i) = 0 THEN
--      RAISE NOTICE 'sql_leftmost_zero: %', i;
      RETURN i;
  END IF;
END LOOP;
RETURN len; 
END;
$$ LANGUAGE PLPGSQL;

DROP FUNCTION IF EXISTS sql_fmsketch_iter(bytea, anyelement) cascade;
CREATE FUNCTION sql_fmsketch_iter(bitmaps bytea, input anyelement) RETURNS bytea AS
$$
DECLARE
  c bytea;
  nmap CONSTANT integer := 256;
  index integer := 0;
  hashlen integer;
BEGIN
--  RAISE NOTICE 'bitmaps is % long', length(bitmaps);
  -- run SHA-1 on string.
  c := digest(input::text,'SHA1');
  hashlen := length(c)*8;
  -- choose a bitmap by mod'ing the first bigint worth of hash value bits
  index := ((get_byte(c, 7)::bigint) + (get_byte(c, 6)::bigint << 3) + (get_byte(c,5)::bigint << 6) + (get_byte(c,4)::bigint << 9) + (get_byte(c,3)::bigint << 12) + (get_byte(c,2)::bigint << 15) + (get_byte(c,1)::bigint << 18) + ((get_byte(c,0)/2)::bigint:: bigint << 21)
  ) % nmap;
--  RAISE NOTICE 'index is: %', index;
--  RAISE NOTICE 'rmost is: %', sql_rightmost_one(c);
  return set_bit(bitmaps, index*hashlen + (hashlen - 1) - sql_rightmost_one(c), 1);
--    return flipbit(bitmaps, nmap, 120, index + (hashlen - 1) - sql_rightmost_one(c));
END;
$$
LANGUAGE PLPGSQL;


DROP FUNCTION IF EXISTS sql_fmsketch_getcount(bytea) cascade;
CREATE FUNCTION sql_fmsketch_getcount(bitmaps bytea) RETURNS INTEGER AS
$$
DECLARE
  nmap CONSTANT integer = 256;
  R integer := 0;
  S integer := 0;
  phi CONSTANT float := 0.77351; -- the magic constant
  out text := '';
BEGIN
  FOR i in 0..(nmap-1) LOOP
    out = '';
    FOR R in 0..(20*8-1) LOOP
      out = out || get_bit(bitmaps, i*20*8 + R);
    END LOOP;
--   RAISE NOTICE 'bitmap %: %.  adding % to S', i, out, sql_leftmost_zero(substring(bitmaps from i*20+1 for 20));
--    RAISE NOTICE 'substring(bitmaps from %*20+1 for 20) is %', i, substring(bitmaps from i*20+1 for 20); 
    S := S + sql_leftmost_zero(substring(bitmaps from i*20+1 for 20));
  END LOOP;
--  RAISE NOTICE ' (256/0.77351) * 2^(%/256)', S::float;
  RETURN ceil((nmap::float/phi) * pow(2.0,(S::float)/(nmap::float)));
END;
$$ 
LANGUAGE PLPGSQL;

