-- The below implement the bitmaps within a Postgres Array



DROP FUNCTION IF EXISTS sql_fmsketch_array_iter(bytea[], anyelement) cascade;
CREATE FUNCTION sql_fmsketch_array_iter(bitmaps bytea[], input anyelement) RETURNS bytea[] AS
$$
DECLARE
  c bytea;
  nmap CONSTANT integer := 64;
  index integer := 0;
  emptyhash CONSTANT bytea := E'\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000\\000'::bytea;
  newmaps bytea[];
BEGIN
  -- run SHA-1 on string.
  c := digest(input::text,'SHA1');
  -- choose a bitmap by mod'ing the hash value
  index := get_byte(c, 0) % nmap;
  --    for each of the bitmaps
  FOR i in 1..(nmap) LOOP
    -- //  during the insertion we insert each element
    -- // in one bitmap only (a la Flajolet pseudocode, page 16).
    -- Find index of the sql_rightmost non-0 bit.  Turn on that bit (from left!) in the sketch.
    IF i = index THEN
       IF bitmaps[i] IS NOT NULL THEN
         newmaps = array_append(newmaps, set_bit(bitmaps[i], (length(bitmaps[i])*8 - 1) - sql_rightmost_one(c), 1));
       ELSE
         newmaps = array_append(newmaps, set_bit(emptyhash, (length(emptyhash)*8 - 1) - sql_rightmost_one(c), 1));        
       END IF;
    ELSE
       IF bitmaps[i] IS NOT NULL THEN newmaps = array_append(newmaps, bitmaps[i]);
       ELSE newmaps = array_append(newmaps, emptyhash);
       END IF;
    END IF;
  END LOOP;
  return newmaps;
END;
$$
LANGUAGE PLPGSQL;

DROP FUNCTION IF EXISTS sql_fmsketch_array_to_text(bytea[]) cascade;
CREATE FUNCTION sql_fmsketch_array_to_text(bitmaps bytea[]) RETURNS text AS
$$
DECLARE
  nmap CONSTANT integer = 64;
  out text = '';
  tmp text = '';
BEGIN
  FOR i in 0..(nmap-1) LOOP
    out = out || '|' || bitmaps[i];
  END LOOP;
  RETURN out;
END;
$$ 
LANGUAGE PLPGSQL;

CREATE AGGREGATE sql_fmsketch_array(anyelement)
(
    sfunc = sql_fmsketch_array_iter,
    stype = bytea[], 
    finalfunc = sql_fmsketch_array_to_text
);


DROP FUNCTION IF EXISTS sql_fmsketch_array_getcount(bytea[]) cascade;
CREATE FUNCTION sql_fmsketch_array_getcount(bitmaps bytea[]) RETURNS INTEGER AS
$$
DECLARE
  nmap CONSTANT integer = 64;
  R integer := 0;
  S integer := 0;
  phi CONSTANT float := 0.77351; -- the magic constant
  out text := '';
BEGIN
  FOR i in 0..(nmap-1) LOOP
  out = '';
    CONTINUE WHEN bitmaps[i] IS NULL;
    FOR R in 0..length(bitmaps[i])-1 LOOP
      out = out || get_bit(bitmaps[i], R);
    END LOOP;
--    RAISE NOTICE 'bitmap %: %.  adding % to S', i, out, sql_leftmost_zero(bitmaps[i]);
    S := S + sql_leftmost_zero(bitmaps[i]);
  END LOOP;
--  RAISE NOTICE ' (64/0.77351) * 2^(%/64)', S::float;
  RETURN ceil((nmap::float/phi) * pow(2.0,(S::float)/(nmap::float)));
END;
$$ 
LANGUAGE PLPGSQL;

  
CREATE AGGREGATE sql_fmcount_array(anyelement)
(
    sfunc = sql_fmsketch_array_iter,
    stype = bytea[], 
    finalfunc = sql_fmsketch_array_getcount
);
